SET client_encoding TO 'UTF8';

SET search_path = genesis,public;
\set ON_ERROR_STOP ON

FUNCTION         ft_celular(
    p_id_persona IN number
) 
RETURN varchar2
IS
    r_numero varchar2(100);
   
BEGIN

	SELECT  TO_char(pt.NUM_TELEFONO) INTO  r_numero 
	FROM genesis.PERSONA_TELEFONO pt 
	WHERE pt.id_persona = p_id_persona AND  ES_ACTIVO =1 AND rownum = 1;
    RETURN r_numero;
END;

