SET client_encoding TO 'UTF8';

SET search_path = genesis,public;
\set ON_ERROR_STOP ON

FUNCTION         ft_cita(
    p_id_persona_interes IN number
) 
RETURN date
IS
    r_fecha date;
   

   
BEGIN
	SELECT t.fecha_Cita INTO  r_fecha  FROM (
	SELECT  fecha_cita , row_number() over ( order by FECHA_CITA asc ) as rnum  
	FROM genesis.PERSONA_INTERES_CITA pic 
	WHERE pic.ID_PERSONA_INTERES = p_id_persona_interes AND  EStado='0' 
	ORDER BY  fecha_Cita asc) t WHERE t.rnum=1 
	
	
	;
    RETURN r_fecha;
END;

