SET client_encoding TO 'UTF8';

SET search_path = genesis,public;
\set ON_ERROR_STOP ON

function         ft_contacto(
p_id_persona_interes IN integer

) 
return varchar2
is
  p_resultado varchar2(500);
begin  
	
	 SELECT detalle INTO p_resultado FROM genesis.PERSONA_ATENCION pa 
	 INNER JOIN genesis.CANAL_DETALLE cd ON cd.ID_CANAL_DETALLE =pa.ID_CANAL_DETALLE 
	WHERE pa.ID_PERSONA_INTERES =p_id_persona_interes AND rownum=1
AND cd.CODIGO ='CNC';

  
  return p_resultado;
  
end;

