SET client_encoding TO 'UTF8';

SET search_path = genesis,public;
\set ON_ERROR_STOP ON

Function         ft_resp_financiero_correo
   ( p_documento IN varchar )
   RETURN varchar
IS
   p_resultado varchar(100);

    

BEGIN

   SELECT rf.CORREO  INTO p_resultado
   FROM genesis.PERSONA_DOCUMENTO pd
   INNER JOIN genesis.RESPONSABLE_FINANCIERO rf ON rf.ID_PERSONA = pd.ID_PERSONA
   WHERE pd.NUM_DOCUMENTO = p_documento;
   

RETURN p_resultado;

END;

