CREATE OR REPLACE FUNCTION jairo.fc_campus (P_ID_MAPA bigint) RETURNS varchar AS $body$
DECLARE

    L_CAMPUS varchar(256);

BEGIN 
    SELECT C.NOMBRE INTO STRICT L_CAMPUS
    FROM SGE_MAPA A 
    LEFT JOIN ELISEO.ORG_SEDE_AREA B ON A.ID_SEDEAREA = B.ID_SEDEAREA 
    LEFT JOIN ELISEO.ORG_SEDE C ON B.ID_SEDE  = C.ID_SEDE 
    LEFT JOIN ELISEO.CONTA_ENTIDAD_DEPTO C ON B.ID_ENTIDAD = C.ID_ENTIDAD 
    AND B.ID_DEPTO = C.ID_DEPTO
    WHERE A.ID_MAPA = P_ID_MAPA;

    RETURN(L_CAMPUS);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
