CREATE OR REPLACE FUNCTION jairo.fc_completo_pasos ( P_ID_INICIATIVA_DETALLE JAIRO.SGE_MAPA_INICIATIVA_DETALLE.ID_INICIATIVA_DETALLE%TYPE) RETURNS varchar AS $body$
DECLARE

    r smallint := 0;

BEGIN
    IF (P_ID_INICIATIVA_DETALLE IS NOT NULL AND P_ID_INICIATIVA_DETALLE::text <> '')
    THEN

        SELECT (CASE
                    WHEN EXISTS (SELECT SMID2.ID_DEXEC
                                FROM JAIRO.SGE_MAPA_INICIATIVA_DETALLE SMID2
                                         INNER JOIN ELISEO.LAMB_PROCESS_EXEC_PASO LPEP2 ON SMID2.ID_DEXEC = LPEP2.ID_DEXEC
                                         INNER JOIN ELISEO.LAMB_PROCESS_EXECUTE LPE3 ON LPEP2.ID_EXECUTE = LPE3.ID_EXECUTE
                                         INNER JOIN ELISEO.LAMB_PROCESS_PASO LPP2 ON LPEP2.ID_PASO_ACTUAL = LPP2.ID_PASO
                                         INNER JOIN ELISEO.TIPO_ORIGEN TO245 ON LPE3.ID_TIPOORIGEN = TO245.ID_TIPOORIGEN
                                WHERE SMID2.ID_INICIATIVA_DETALLE = P_ID_INICIATIVA_DETALLE
                                  AND LPP2.CODIGO = 'step2'
                                  AND TO245.NOMBRE_TABLA = 'SGE_MAPA_INICIATIVA_DETALLE') THEN 1
                    ELSE 0 END)
        INTO STRICT r
;

    END IF;

    RETURN r;
END;
$body$
LANGUAGE PLPGSQL
