CREATE OR REPLACE FUNCTION jairo.fc_inf_id_iniciativa_detalle ( P_ID_INICIATIVA_DETALLE JAIRO.SGE_MAPA_INICIATIVA_DETALLE.ID_INICIATIVA_DETALLE%TYPE) RETURNS bigint AS $body$
DECLARE

    RET JAIRO.SGE_MAPA_INICIATIVA_DETALLE.ID_INICIATIVA_DETALLE%TYPE;

BEGIN

    IF (P_ID_INICIATIVA_DETALLE IS NOT NULL AND P_ID_INICIATIVA_DETALLE::text <> '') THEN

        SELECT SMID_INF.ID_INICIATIVA_DETALLE
        INTO STRICT RET
        FROM JAIRO.SGE_MAPA_INICIATIVA_DETALLE SMID
                 LEFT JOIN JAIRO.SGE_MAPA_INICIATIVA_DETALLE SMID_INF
                           ON SMID_INF.ID_MAPA_INICIATIVA = SMID.ID_MAPA_INICIATIVA AND
                              SMID_INF.SEMESTRE = SMID.SEMESTRE AND SMID_INF.ID_SEMESTRE_I = SMID.ID_SEMESTRE_I AND
                              SMID_INF.ID_SEMESTRE_II = SMID.ID_SEMESTRE_II AND SMID_INF.TIPO = 2
        WHERE SMID.ID_INICIATIVA_DETALLE = P_ID_INICIATIVA_DETALLE
          AND SMID.TIPO = 1;
    END IF;

    RETURN RET;
END;
$body$
