CREATE OR REPLACE FUNCTION jairo.fc_iniciativa_presupuesto ( P_ID_INICIATIVA_DETALLE integer, P_FILTRO_ACTIVIDAD integer DEFAULT NULL) RETURNS bigint AS $body$
DECLARE
 PS decimal(10,2);

BEGIN
    SELECT SUM(SA.PRESUPUESTO) INTO STRICT PS
        FROM JAIRO.SGE_ACTIVIDAD SA
        WHERE SA.ID_INICIATIVA_DETALLE = P_ID_INICIATIVA_DETALLE
        AND (coalesce(P_FILTRO_ACTIVIDAD::text, '') = '' OR SA.TIENE_PPTO = (P_FILTRO_ACTIVIDAD)::numeric );
return PS;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
