CREATE OR REPLACE FUNCTION jairo.iniciativa_presupuesto ( P_ID_MAPA_INICIATIVA integer, P_FILTRO_ACTIVIDAD integer DEFAULT NULL) RETURNS bigint AS $body$
DECLARE

    PS decimal(10,2);

BEGIN
    SELECT SUM(SA.PRESUPUESTO) INTO STRICT PS
    FROM SGE_ACTIVIDAD SA
    WHERE SA.ID_MAPA_INICIATIVA = (P_ID_MAPA_INICIATIVA)::numeric
      AND (coalesce(P_FILTRO_ACTIVIDAD::text, '') = '' OR SA.TIENE_PPTO = (P_FILTRO_ACTIVIDAD)::numeric );

    RETURN PS;
EXCEPTION
    WHEN OTHERS THEN
        RETURN NULL;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
