SET client_encoding TO 'UTF8';

SET search_path = jairo,public;
\set ON_ERROR_STOP ON

FUNCTION FC_DEPARTMENTO(P_ID_MAPA NUMBER) RETURN VARCHAR2 IS
    L_DEPARTAMENTO VARCHAR2(256);
    BEGIN 
    SELECT B.ID_DEPTO||' - '||C.NOMBRE INTO L_DEPARTAMENTO
    FROM SGE_MAPA A 
    LEFT JOIN ELISEO.ORG_SEDE_AREA B ON A.ID_SEDEAREA = B.ID_SEDEAREA 
    LEFT JOIN ELISEO.CONTA_ENTIDAD_DEPTO C ON B.ID_ENTIDAD = C.ID_ENTIDAD 
    AND B.ID_DEPTO = C.ID_DEPTO
    WHERE A.ID_MAPA = P_ID_MAPA;
    RETURN(L_DEPARTAMENTO); 
END;

