FUNCTION INICIATIVA_ESTADO(P_ID_DEXEC IN INTEGER)
RETURN VARCHAR2 IS RET VARCHAR2(100);
BEGIN
    SELECT LPP.GLOSA INTO RET
        FROM ELISEO.LAMB_PROCESS_EXEC_PASO LPEP
                 INNER JOIN ELISEO.LAMB_PROCESS_PASO LPP ON LPEP.ID_PASO_ACTUAL = LPP.ID_PASO
        WHERE LPEP.ID_DEXEC = P_ID_DEXEC;
return RET;
END;

