SET client_encoding TO 'UTF8';

SET search_path = jairo,public;
\set ON_ERROR_STOP ON

FUNCTION       INICIATIVA_PRESUPUESTO(
    P_ID_MAPA_INICIATIVA IN INTEGER,
    P_FILTRO_ACTIVIDAD IN INTEGER DEFAULT NULL
)
RETURN NUMBER IS 
    PS NUMBER(10, 2);
BEGIN
    SELECT SUM(SA.PRESUPUESTO) INTO PS
    FROM SGE_ACTIVIDAD SA
    WHERE SA.ID_MAPA_INICIATIVA = TO_NUMBER(P_ID_MAPA_INICIATIVA)
      AND (P_FILTRO_ACTIVIDAD IS NULL OR SA.TIENE_PPTO = TO_NUMBER(P_FILTRO_ACTIVIDAD));

    RETURN PS;
EXCEPTION
    WHEN OTHERS THEN
        RETURN NULL;
END;

