CREATE OR REPLACE PROCEDURE moises.insert_pers_nat_resp_fin ( v_id_persona text, v_nombre text, v_paterno text, v_materno text, out CURSOR REFCURSOR ) AS $body$
DECLARE


p_id_persona integer:=0;
p_id_persona_natural integer:=0;
p_id_persona_natural_resp_fin integer:=0;


BEGIN

  select coalesce((select p.id_persona
              from persona p
              where p.id_persona=v_id_persona),0) into STRICT  p_id_persona
;

  if (p_id_persona>0) then
    
    select coalesce((select p.id_persona
              from persona_natural p 
              where p.id_persona=p_id_persona),0) into STRICT p_id_persona_natural
;

    if (p_id_persona_natural>0) then
    
        select coalesce((select id_persona
                    from persona_natural_resp_fin p 
                    where p.id_persona=p_id_persona_natural),0) into STRICT p_id_persona_natural_resp_fin
;

        if (p_id_persona_natural_resp_fin=0) then
        
            insert into persona_natural_resp_fin(id_persona)
            values (p_id_persona_natural);

        end if;

    else
            
            insert into persona_natural(id_persona)
            values (p_id_persona);

            insert into persona_natural_resp_fin(id_persona)
            values (p_id_persona);

    end if;

  else
  
  select max(id_persona)+1 into STRICT p_id_persona
    from persona;

    insert into persona(id_persona,nombre,paterno,materno)
    values (p_id_persona,v_nombre,v_paterno,v_materno);

    insert into persona_natural(id_persona)
    values (p_id_persona);

    insert into persona_natural_resp_fin(id_persona)
    values (p_id_persona);

  end if;

  open CURSOR FOR 
    SELECT pn.id_persona,p.nombre,p.paterno,p.materno,pn.id_tipodocumento,pn.num_documento
    from persona_natural pn inner join persona p on p.id_persona=pn.id_persona
    where pn.id_persona=p_id_persona;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
