CREATE OR REPLACE VIEW vw_persona_natural_trabajador (id_persona, paterno, materno, nombre, sexo, fec_nacimiento, nom_persona, id_tipodocumento, num_documento, id_entidad, id_depto, id_cargo, cargo, estado) AS SELECT DISTINCT Persona.ID_PERSONA,
Persona.PATERNO,
Persona.MATERNO,
Persona.NOMBRE,
Persona_Natural.SEXO,
Persona_Natural.FEC_NACIMIENTO,
UPPER(coalesce(Persona.PATERNO || ' ', '')|| coalesce(Persona.MATERNO || ', ', ''))||Persona.NOMBRE AS NOM_PERSONA,
Persona_Documento.ID_TIPODOCUMENTO,
Persona_Documento.NUM_DOCUMENTO,
null as ID_ENTIDAD,
null as ID_DEPTO,
null as ID_CARGO,
null AS CARGO,
null as ESTADO
