FROM Persona INNER JOIN Persona_Natural
ON Persona.ID_PERSONA = Persona_Natural.ID_PERSONA
INNER JOIN Persona_Documento
ON Persona.ID_PERSONA = Persona_Documento.ID_PERSONA
