SET client_encoding TO 'UTF8';

SET search_path = moises,public;
\set ON_ERROR_STOP ON

CREATE OR REPLACE VIEW vw_persona_natural_legal (id_persona, id_ruc, nombre) AS SELECT ID_PERSONA, id_ruc AS ID_RUC, nombre FROM vw_persona_juridica

UNION
 
                        SELECT P.ID_PERSONA, PD.NUM_DOCUMENTO AS ID_RUC, UPPER(coalesce(P.PATERNO || ' ' , '') || coalesce(P.MATERNO || ', ', '')) || P.NOMBRE AS nombre 
                        FROM MOISES.PERSONA P
                        INNER JOIN MOISES.PERSONA_DOCUMENTO PD ON PD.ID_PERSONA = P.ID_PERSONA
                        WHERE PD.ID_TIPODOCUMENTO = 6;

