FUNCTION        FC_NOMBRE_PERSONA_TEMP(P_ID_PERSONA NUMBER) RETURN VARCHAR2 AS
  l_datos VARCHAR2(250);
 cursor datos is
                SELECT  
                UPPER(NVL(PATERNO || ' ' , '') || NVL(MATERNO || ', ', '')) || Persona.NOMBRE AS NOM_PERSONA
                FROM moises.PERSONA 
                WHERE ID_PERSONA=P_ID_PERSONA;
        
BEGIN
       open datos;  
       fetch datos into l_datos;
       if not datos%found then
         l_datos := ' ';
       end if;
       close datos;
       RETURN( l_datos );
END;

