SET client_encoding TO 'UTF8';

SET search_path = moises,public;
\set ON_ERROR_STOP ON

FUNCTION ft_codigo_verificacion RETURN VARCHAR2 IS
   p_codigo  VARCHAR2(200);
   p_res  VARCHAR2(200);
begin

p_codigo := dbms_random.string('X', 5);

select nvl(ft_verificacion(p_codigo,'1'),'N') into p_res
    from dual;


WHILE p_res = 'S'
LOOP

   p_codigo := dbms_random.string('X', 5);
    
   select nvl(ft_verificacion(p_codigo,'1'),'N') into p_res
    from dual;
    
END LOOP;

return p_codigo;

end;

